statements.sql:
Declare @TmpTable Table
(order_id nvarchar(100)
,row_no int
);

insert into @TmpTable (order_id,row_no)
select x.order_id ,x.rownum_
From ( select rownum_=row_number() over(order by a.order_no)
,order_id
from t_adjustment_order a

where 1=1



      a.order_no like '%' +  #OrderNo# + '%'





    a.customer_id like '%' +  #customer_id# + '%'





      a.order_date like '%' + #OrderDate# + '%'





      a.order_date    >= #BeginDate#





      a.order_date    <= #EndDate#





      a.item_price_type_id =   #ItemPriceTypeId#





      a.status =   #Status#




) x ;
Declare @iCount int;
select @iCount = COUNT(*) From @TmpTable;

statements.statement:
update T_Adjustment_Order set order_date = #order_date# ,item_price_type_id = #item_price_type_id# ,begin_date = #begin_date# ,end_date = #end_date# ,remark = #remark# ,status = #status# ,status_desc = #status_desc# ,create_user_id = #create_user_id# ,create_time = #create_time# ,modify_user_id = #modify_user_id# ,modify_time = #modify_time# where order_id = #order_id#;
insert into T_Adjustment_Order ( order_id ,order_no ,order_date ,item_price_type_id ,begin_date ,end_date ,remark ,status ,status_desc ,create_user_id ,create_time ,modify_user_id ,modify_time,customer_id)
select a.* From ( select #order_id# order_id ,#order_no# order_no ,#order_date# order_date ,#item_price_type_id# item_price_type_id ,#begin_date# begin_date ,#end_date# end_date ,#remark# remark ,#status# status ,#status_desc# status_desc ,#create_user_id# create_user_id ,#create_time# create_time ,#modify_user_id# modify_user_id ,#modify_time# modify_time ,#customer_id# customer_id ) a left join T_Adjustment_Order b on(a.order_id = b.order_id) where b.order_id is null ;

statements.select:
Declare @TmpTable Table (order_id nvarchar(100) ,row_no int );
insert into @TmpTable (order_id,row_no) select a.order_id ,row_number() over(order by a.no) row_no From T_Adjustment_Order a inner join T_Adjustment_Order_Detail_Unit b on(a.order_id = b.order_id) inner join vw_unit_level c on(b.unit_id = c.unit_id) where a.status = '10'
c.path_unit_id like '%' + #UnitId# + '%'
a.item_price_type_id = #ItemPriceTypeId#
a.no > #No#
;
select a.order_id ,a.order_no ,a.order_date ,a.begin_date ,a.end_date ,a.item_price_type_id ,a.remark ,a.status ,a.status_desc ,a.create_user_id ,a.create_time ,a.modify_user_id ,a.modify_time ,(select USER_NAME From T_User where user_id = a.create_user_id) create_user_name ,(select USER_NAME From T_User where user_id = a.modify_user_id) modify_user_name ,(select item_price_type_name From T_Item_Price_Type where item_price_type_id = a.item_price_type_id) item_price_type_name ,a.no From T_Adjustment_Order a inner join @TmpTable b on(a.order_id = b.order_id) where 1=1 and b.row_no > #StartRow# and b.row_no <=#EndRow# order by a.no ;
