statements.statement:
update T_Advertise set advertise_name = #advertise_name# ,advertise_code = #advertise_code# ,file_size = #file_size# ,file_format = #file_format# ,display = #display# ,playback_time = #playback_time# ,url_address = #url_address# ,brand_customer_id = #brand_customer_id# ,brand_id = #brand_id# ,status = #status#
,modify_user_id = #modify_user_id# ,modify_time = #modify_time# where advertise_id = #advertise_id#;
insert into T_Advertise( advertise_id ,advertise_name ,advertise_code ,file_size ,file_format ,display ,playback_time ,url_address ,brand_customer_id
,status ,create_user_id ,create_time ,modify_user_id ,modify_time ,brand_id )
select a.* From ( select #advertise_id# advertise_id ,#advertise_name# advertise_name ,#advertise_code# advertise_code ,#file_size# file_size ,#file_format# file_format ,#display# display ,#playback_time# playback_time ,#url_address# url_address ,#brand_customer_id# brand_customer_id
,#status# status ,#create_user_id# create_user_id ,#create_time# create_time ,#modify_user_id# modify_user_id ,#modify_time# modify_time ,#brand_id# brand_id ) a left join T_Advertise b on(a.advertise_id = b.advertise_id) where b.advertise_id is null and a.advertise_id not in (select advertise_id from T_Advertise);
