statements.statement:
update T_Advertise_Order set order_date = #order_date# ,date_end = #date_end# ,time_start = #time_start# ,time_end = #time_end# ,url_address = #url_address# ,playbace_no = #playbace_no# ,status = #status# ,status_desc = #status_desc#
,modify_user_id = #modify_user_id# ,modify_time = #modify_time# where order_id = #order_id#;
insert into T_Advertise_Order ( order_id ,order_code ,order_date ,date_start ,date_end ,time_start ,time_end ,playbace_no ,url_address ,customer_id ,status ,status_desc ,create_user_id ,create_time ,modify_user_id ,modify_time )
select a.* From ( select #order_id# order_id ,#order_code# order_code ,#order_date# order_date ,#date_start# date_start ,#date_end# date_end ,#time_start# time_start ,#time_end# time_end ,#playbace_no# playbace_no ,#url_address# url_address
,#customer_id# customer_id ,#status# status ,#status_desc# status_desc ,#create_user_id# create_user_id ,#create_time# create_time ,#modify_user_id# modify_user_id ,#modify_time# modify_time
) a left join T_Advertise_Order b on(a.order_id = b.order_id) where b.order_id is null ;

statement.isNotEmpty:
insert into T_Advertise_Order_Advertise ( id ,order_id ,advertise_id )
SELECT a.id ,a.order_id ,a.advertise_id
FROM (
SELECT #advertiseOrderAdvertiseInfoList[].id# id ,#advertiseOrderAdvertiseInfoList[].order_id# order_id ,#advertiseOrderAdvertiseInfoList[].advertise_id# advertise_id ,#advertiseOrderAdvertiseInfoList[].customer_id# customer_id
) a left join T_Advertise_Order_Advertise b on(a.id = b.id) where b.id is null;

insert into T_Advertise_Order_Unit ( order_unit_id ,order_id ,unit_id ,customer_id ) SELECT a.order_unit_id ,a.order_id ,a.unit_id ,c.customer_id
FROM (
SELECT #advertiseOrderUnitInfoList[].order_unit_id# order_unit_id ,#advertiseOrderUnitInfoList[].order_id# order_id ,#advertiseOrderUnitInfoList[].unit_id# unit_id ,#advertiseOrderUnitInfoList[].customer_id# customer_id
) a left join T_Advertise_Order_Unit b on(a.order_unit_id = b.order_unit_id) left join t_unit c on(b.unit_id = c.unit_id) where b.order_unit_id is null;

statements.select:
Declare @TmpTable Table (order_id nvarchar(100) ,row_no int );
insert into @TmpTable (order_id,row_no) select a.order_id ,row_number() over(order by a.order_code) row_no From T_Advertise_Order a inner join T_Advertise_Order_Unit b on(a.order_id = b.order_id) where 1=1 and isnull(b.if_flag,'0') = '0' and b.unit_id = #UnitId#;
select a.order_id ,a.order_code ,a.order_date ,a.date_start ,a.date_end ,a.time_start ,a.time_end ,a.playbace_no ,a.url_address ,a.customer_id ,a.status ,a.status_desc ,a.create_user_id ,a.create_time ,a.modify_time ,a.modify_user_id ,(select USER_NAME From T_User x where x.user_id = a.create_user_id) create_user_name ,(select USER_NAME From T_User x where x.user_id = a.modify_user_id) modify_user_name From t_advertise_order a inner join @TmpTable b on(a.order_id = b.order_id) where 1=1 and b.row_no > #StartRow# and b.row_no <=#EndRow# order by order_code;

update.isNotEmpty:
update T_Advertise_Order_Unit set bat_no = #bat_no# From T_Advertise_Order_Unit x inner join ( SELECT order_id ,unit_id FROM (
SELECT #advertiseOrderUnitInfoList[].order_id# order_id ,#advertiseOrderUnitInfoList[].unit_id# unit_id
) a ) y on(x.order_id = y.order_id and x.unit_id = y.unit_id);

statements.update:
update T_Advertise_Order_Unit
set if_flag = '1'
where bat_no = #bat_no#;
