statements.sql:
Declare @TmpTable Table
(item_id nvarchar(100)
,row_no int
);

insert into @TmpTable (item_id,row_no)
select x.item_id ,x.rownum_
From ( select rownum_=row_number() over(order by a.item_code)
,item_id
from t_item a
where 1=1



      a.item_code like '%' +  #item_code# + '%'





      a.item_name like '%' +  #item_name# + '%'





      a.pyzjm like '%' + #pyzjm# + '%'





      a.status =  #status#





      (a.item_category_id =  #item_category_id#
      or a.item_category_id in (select item_category_id From vw_item_category_level where path_item_category_id like '%' + #item_category_id# + '%' )
      )




) x ;

Declare @iCount int;
select @iCount = COUNT(*) From @TmpTable;

statements.select:
select count(*) From t_item where 1=1
item_id != #ItemId#
and item_code = #ItemCode#;

select count(*) From t_sku where 1=1
sku_id != #SkuId#
and barcode = #Barcode#;

statements.statement:
update T_Item set item_category_id = #Item_Category_Id# , item_code = #Item_Code# , item_name = #Item_Name# ,item_name_en = #Item_Name_En# , item_name_short = #Item_Name_Short# ,pyzjm = #Pyzjm# ,item_remark = #Item_Remark# ,modify_user_id = #Modify_User_Id# ,modify_time = #Modify_Time# ,ifgifts = #ifgifts# ,ifoften = #ifoften# ,ifservice = #ifservice# ,if_flag = '0' ,isGB = #isGB# ,data_from = '3' ,display_index = #display_index# where item_id = #Item_Id#;
insert into T_Item ( item_id ,item_category_id ,item_code ,item_name ,item_name_en ,item_name_short ,pyzjm ,item_remark ,status ,status_desc ,create_user_id ,create_time ,modify_user_id ,modify_time ,ifgifts ,ifoften ,ifservice ,a.isGB ,a.data_from ,a.display_index )
select a.* From ( select #Item_Id# item_id ,#Item_Category_Id# item_category_id ,#Item_Code# item_code ,#Item_Name# item_name ,#Item_Name_En# item_name_en ,#Item_Name_Short# item_name_short ,#Pyzjm# pyzjm ,#Item_Remark# item_remark ,#Status# status ,#Status_Desc# status_desc ,#Create_User_Id# create_user_id ,#Create_Time# create_time ,#Modify_User_Id# modify_user_id ,#Modify_Time# modify_time ,#ifgifts# ifgifts ,#ifoften# ifoften ,#ifservice# ifservice ,#isGB# isGB ,'3' data_from ,#display_index# display_index ) a left join T_Item b on(a.item_id = b.item_id) where b.item_id is null ;

statements.select:
select top 10 item_id,item_name From t_item a where status = '1' and 1=1 and (
a.item_code like '%' + #ItemName# + '%'
a.item_name like '%' + #ItemName# + '%'
) order by a.item_code;

select count(*) From t_item where isnull(if_flag,'0') = '0' and data_from in ('2','3')
customerId = #customerId#
;

Declare @TmpTable Table (item_id nvarchar(100) ,row_no int );
insert into @TmpTable (item_id,row_no) select item_id ,row_number() over(order by item_code) row_no From T_Item where 1=1 and isnull(if_flag,'0') = '0' and data_from in ('2','3')
customerId = #customerId#
;
select a.item_id ,a.item_category_id ,a.item_code ,a.item_name ,a.item_name_en ,a.item_name_short ,a.pyzjm ,a.item_remark ,a.status ,a.status_desc ,a.create_user_id ,a.create_time ,a.modify_user_id ,a.modify_time ,(select item_category_name from T_Item_Category x where x.item_category_id = a.item_category_id) item_category_name ,(select item_category_code from T_Item_Category x where x.item_category_id = a.item_category_id) item_category_code ,(select user_name from t_user x where x.user_id = a.create_user_id) create_user_name ,(select user_name from t_user x where x.user_id = a.modify_user_id) modify_user_name ,case when a.status = '1' then '正常' else '删除' end status_desc ,a.ifgifts ,a.ifoften ,a.ifservice ,a.isGB ,a.data_from ,a.display_index ,a.imageUrl ,a.customerId From t_item a inner join @TmpTable b on(a.item_id = b.item_id) where b.row_no > #StartRow# and b.row_no <=#EndRow# order by a.item_code ;
