statements.sql:
Declare @TmpTable Table
(item_category_id nvarchar(100)
,row_no int
);

insert into @TmpTable (item_category_id,row_no)
select x.item_category_id ,x.rownum_
From ( select rownum_=row_number() over(order by a.item_category_code)
,item_category_id
from t_item_category a
where 1=1



      a.item_category_code like '%' +  #item_category_code# + '%'





      a.item_category_name like '%' +  #item_category_name# + '%'





      a.pyzjm like '%' + #pyzjm# + '%'





      a.status =  #status#



) x ;
Declare @iCount int;
select @iCount = COUNT(*) From @TmpTable;

statements.statement:
update T_Item_Category set item_category_code = #Item_Category_Code# , item_category_name = #Item_Category_Name# , pyzjm = #Pyzjm# , status = #Status# , parent_id = #Parent_Id# , modify_user_id = #Modify_User_Id# , modify_time = #Modify_Time# where item_category_id = #Item_Category_Id#;
insert into T_Item_Category ( item_category_id , item_category_code , item_category_name , pyzjm , status , parent_id , create_user_id , create_time , modify_user_id , modify_time)
select a.* From ( select #Item_Category_Id# item_category_id , #Item_Category_Code# item_category_code , #Item_Category_Name# item_category_name , #Pyzjm# pyzjm , #Status# status , #Parent_Id# parent_id , #Create_User_Id# create_user_id , #Create_Time# create_time , #Modify_User_Id# modify_user_id , #Modify_Time# modify_time ) a left join T_Item_Category b on(a.item_category_id = b.item_category_id) where b.item_category_id is null ;

statements.select:
select count(*) From t_item_category where 1=1
item_category_id != #Item_Category_Id#
and item_category_code = #Item_Category_Code#;

select count(*) From T_Item_Category where isnull(if_flag,'0') = '0'
customerId = #customerId#
;
