statements.statement:
update t_sku set status= '-1' ,modify_user_id = #Create_User_Id# ,modify_time = #Create_Time# ,if_flag = '0'
where t_sku.item_id = #Item_Id#;
update t_sku set sku_prop_id1 = a.prop_1_detail_id ,sku_prop_id2 = a.prop_2_detail_id ,sku_prop_id3 = a.prop_3_detail_id ,sku_prop_id4 = a.prop_4_detail_id ,sku_prop_id5 = a.prop_5_detail_id ,barcode = a.barcode ,modify_user_id = a.modify_user_id ,modify_time = a.modify_time ,status = '1' ,if_flag = '0' From (
SELECT #Item_Id# item_id , #Create_User_Id# modify_user_id , #Create_Time# modify_time , #SkuList[].sku_id# sku_id , #SkuList[].prop_1_detail_id# prop_1_detail_id , #SkuList[].prop_2_detail_id# prop_2_detail_id , #SkuList[].prop_3_detail_id# prop_3_detail_id , #SkuList[].prop_4_detail_id# prop_4_detail_id , #SkuList[].prop_5_detail_id# prop_5_detail_id , #SkuList[].barcode# barcode
) a where (t_sku.sku_id = a.sku_id);
insert into t_sku (sku_id ,item_id ,sku_prop_id1 ,sku_prop_id2 ,sku_prop_id3 ,sku_prop_id4 ,sku_prop_id5 ,barcode ,status ,create_user_id ,create_time ,modify_user_id ,modify_time ) SELECT P.sku_id ,P.item_id ,P.sku_prop_id1 ,P.sku_prop_id2 ,P.sku_prop_id3 ,P.sku_prop_id4 ,P.sku_prop_id5 ,P.barcode ,P.status ,P.create_user_id ,P.create_time ,P.modify_user_id ,P.modify_time
FROM (
SELECT #Item_Id# item_id , #Create_User_Id# modify_user_id , #Create_Time# modify_time , #SkuList[].sku_id# sku_id , #SkuList[].prop_1_detail_id# sku_prop_id1 , #SkuList[].prop_2_detail_id# sku_prop_id2 , #SkuList[].prop_3_detail_id# sku_prop_id3 , #SkuList[].prop_4_detail_id# sku_prop_id4 , #SkuList[].prop_5_detail_id# sku_prop_id5 , #SkuList[].barcode# barcode , #Create_User_Id# create_user_id , #Create_Time# create_time , '1' status
) P left join t_sku b on(P.sku_id = b.sku_id)
where b.sku_id is null;

statements.select:
select count(*) From t_sku a inner join t_item b on(a.item_id = b.item_id) where isnull(a.if_flag,'0') = '0' and b.data_from in ('2','3')
b.customerId = #customerId#
;

Declare @TmpTable Table (sku_id nvarchar(100) ,row_no int );
insert into @TmpTable (sku_id,row_no) select a.sku_id ,row_number() over(order by a.sku_id) row_no From t_sku a inner join t_item b on(a.item_id = b.item_id) where isnull(a.if_flag,'0') = '0' and b.data_from in ('2','3')
b.customerId = #customerId# ;
select a.sku_id ,item_id ,prop_1_detail_id ,prop_1_detail_name ,prop_2_detail_id ,prop_2_detail_name ,prop_3_detail_id ,prop_3_detail_name ,prop_4_detail_id ,prop_4_detail_name ,prop_5_detail_id ,prop_5_detail_name ,prop_1_detail_code ,prop_2_detail_code ,prop_3_detail_code ,prop_4_detail_code ,prop_5_detail_code ,status ,create_user_id ,create_time ,modify_user_id ,modify_time ,item_name ,item_code ,prop_1_id ,prop_1_name ,prop_2_id ,prop_2_name ,prop_3_id ,prop_3_name ,prop_4_id ,prop_4_name ,prop_5_id ,prop_5_name ,barcode ,prop_1_code ,prop_2_code ,prop_3_code ,prop_4_code ,prop_5_code From vw_sku a inner join @TmpTable b on(a.sku_id = b.sku_id) and b.row_no > #StartRow# and b.row_no <=#EndRow# order by a.item_code,a.barcode ;
