statements.select:
Declare @TmpTable Table (monitor_log_id nvarchar(100) ,row_no int );
insert into @TmpTable (monitor_log_id,row_no) select monitor_log_id ,row_number() over(order by monitor_log_id) row_no From T_Monitor_Log where 1=1 and isnull(if_flag,'0') = '0' and customer_id = #CustomerId# and unit_id = isnull(#UnitId#,unit_id);
select a.monitor_log_id ,a.customer_id ,a.unit_id ,a.pos_id ,a.upload_time ,a.remark ,a.create_user_id ,a.create_time ,a.if_flag From T_Monitor_Log a inner join @TmpTable b on(a.monitor_log_id = b.monitor_log_id) and b.row_no > #StartRow# and b.row_no <=#EndRow# order by a.upload_time ;

update.isNotEmpty:
update T_Monitor_Log
set bat_id = #bat_id#
where monitor_log_id in (

  SELECT #monitorLogInfoList[].monitor_log_id#
 );

statements.update:
update T_Monitor_Log
set if_flag = '1'
where bat_id = #bat_id#;
