statements.select:
$UnitSQL$
declare @tmp_warehouse table (unit_warehouse_id varchar(32), row_no int); declare @tmp_count int;
insert into @tmp_warehouse(row_no, unit_warehouse_id) select rownum_=row_number() over(order by b.wh_code desc, e.unit_code),c.unit_warehouse_id from t_warehouse b, t_unit_warehouse c, @tmp_unit d, t_unit e where b.warehouse_id=c.warehouse_id and c.unit_id=d.unit_id and c.unit_id=e.unit_id
b.wh_code like '%' + #Code# + '%'
b.wh_name like '%' + #Name# + '%'
b.wh_contacter like '%' + #Contacter# + '%'
b.wh_tel like '%' + #Tel# + '%'
b.wh_status = #Status#
;
select @tmp_count=count(*) from @tmp_warehouse;
select @tmp_count as record_count,b.*, case b.wh_status when 1 then '正常' else '停用' end as wh_status_desc, case b.is_default when 1 then '是' else '否' end as is_default_desc, d.unit_id, d.unit_name, d.unit_code, d.unit_name_short from t_warehouse b, t_unit_warehouse c, t_unit d, @tmp_warehouse e where b.warehouse_id=c.warehouse_id and c.unit_id=d.unit_id and c.unit_warehouse_id=e.unit_warehouse_id and e.row_no > #StartRow# and e.row_no <=#EndRow#;
