statement.isNotEmpty:
update t_repair set repair_type_id = x.repair_type_id ,remark = x.remark ,phone = x.phone ,unit_id = x.unit_id ,pos_id = x.pos_id ,pos_sn = x.pos_sn ,status = x.status ,status_desc = x.status_desc ,repair_time = x.repair_time ,repair_user_id = x.repair_user_id ,complete_time = x.complete_time ,complete_user_id = x.complete_user_id ,modify_time = x.modify_time ,modify_user_id = x.modify_user_id
From ( select p.repair_id ,p.repair_type_id ,p.remark ,p.phone ,p.unit_id ,p.pos_id ,p.pos_sn ,p.status ,p.status_desc ,p.repair_time ,p.repair_user_id ,p.response_time ,p.response_user_id ,p.complete_time ,p.complete_user_id ,p.modify_time ,p.modify_user_id FROM (
SELECT #repairInfoList[].repair_id# repair_id ,#repairInfoList[].repair_type_id# repair_type_id ,#repairInfoList[].remark# remark ,#repairInfoList[].phone# phone ,#repairInfoList[].unit_id# unit_id ,#repairInfoList[].pos_id# pos_id ,#repairInfoList[].pos_sn# pos_sn ,#repairInfoList[].status# status ,#repairInfoList[].status_desc# status_desc ,#repairInfoList[].repair_time# repair_time ,#repairInfoList[].repair_user_id# repair_user_id ,#repairInfoList[].response_time# response_time ,#repairInfoList[].response_user_id# response_user_id ,#repairInfoList[].complete_user_id# complete_user_id ,#repairInfoList[].complete_time# complete_time ,#modify_time# modify_time ,#modify_user_id# modify_user_id
) p ) x where (t_repair.repair_id = x.repair_id);

INSERT INTO t_repair ( repair_id ,repair_type_id ,remark ,phone ,unit_id ,pos_id ,pos_sn ,status ,status_desc ,repair_time ,repair_user_id ,response_time ,response_user_id ,complete_time ,complete_user_id ,modify_time ,modify_user_id )
SELECT p.repair_id ,p.repair_type_id ,p.remark ,p.phone ,p.unit_id ,p.pos_id ,p.pos_sn ,p.status ,p.status_desc ,p.repair_time ,p.repair_user_id ,p.response_time ,p.response_user_id ,p.complete_time ,p.complete_user_id ,p.modify_time ,p.modify_user_id FROM (
SELECT #repairInfoList[].repair_id# repair_id ,#repairInfoList[].repair_type_id# repair_type_id ,#repairInfoList[].remark# remark ,#repairInfoList[].phone# phone ,#repairInfoList[].unit_id# unit_id ,#repairInfoList[].pos_id# pos_id ,#repairInfoList[].pos_sn# pos_sn ,#repairInfoList[].status# status ,#repairInfoList[].status_desc# status_desc ,#repairInfoList[].repair_time# repair_time ,#repairInfoList[].repair_user_id# repair_user_id ,#repairInfoList[].response_time# response_time ,#repairInfoList[].response_user_id# response_user_id ,#repairInfoList[].complete_user_id# complete_user_id ,#repairInfoList[].complete_time# complete_time ,#modify_time# modify_time ,#modify_user_id# modify_user_id
) P Left JOIN t_repair a ON (p.repair_Id = a.repair_Id) where a.repair_Id is null;

statements.sql:
Declare @TmpTable Table
(repair_id nvarchar(100)
,row_no int
);

insert into @TmpTable (repair_id,row_no)
select x.repair_id ,x.rownum_ From (
select
rownum_=row_number() over(order by repair_time desc)
,repair_id
from t_repair a
inner join vw_unit_level b
on(a.unit_id = b.unit_id)
where 1=1




      CONVERT(nvarchar(10),a.repair_time,120)    >= #repair_date_begin#





      CONVERT(nvarchar(10),a.repair_time,120)    <= #repair_date_end#





      a.status= #status#





      b.path_unit_id like '%' + #unit_id# + '%'






) x ;

Declare @iCount int;

select @iCount = COUNT(*) From @TmpTable;
