statements.sql:
Declare @TmpTable Table (item_id nvarchar(100) ,item_code nvarchar(100) ,item_name nvarchar(100) ,barcode nvarchar(100) ,std_price decimal(18,4) ,enter_qty decimal(18,4) ,enter_amount decimal(18,4) ,row_no int );
insert into @TmpTable (item_id,item_code,item_name,barcode,std_price,enter_qty,enter_amount,row_no) select x.item_id,x.item_code,x.item_name,x.barcode,x.std_price ,SUM(x.enter_qty) enter_qty ,SUM(x.enter_amount) enter_amount ,row_number() over(order by x.item_id,x.item_code,x.item_code,x.item_name,x.barcode,x.std_price ) row_no From ( select c.item_id ,c.item_code ,c.item_name ,c.barcode ,b.std_price ,b.enter_qty ,b.enter_amount from T_Inout a inner join T_Inout_Detail b on(a.order_id = b.order_id) inner join vw_sku c on(b.sku_id = c.sku_id) where 1=1 and a.order_type_id = '1F0A100C42484454BAEA211D4C14B80F' and a.order_reason_id = '2F6891A2194A4BBAB6F17B4C99A6C6F5'
a.unit_id in ( $UnitIds$ )
c.item_code like '%' + #item_code# + '%'
c.item_name like '%' + #item_name# + '%'
c.barcode like '%' + #barcode# + '%'
a.order_date >= #order_date_begin#
a.order_date <= #order_date_end#
) x group by x.item_id,x.item_code,x.item_code,x.item_name,x.barcode,x.std_price;
