statements.sql:
Declare @TmpTable Table (order_date nvarchar(100) ,unit_id nvarchar(100) ,sales_qty decimal ,sales_amount decimal(18,4) ,row_no int );
insert into @TmpTable (order_date,unit_id,sales_qty,sales_amount,row_no) select a.order_date ,a.unit_id ,COUNT(order_no) sales_qty ,SUM(total_amount) sales_amount ,row_number() over(order by a.order_date,a.unit_id) row_no From (
select order_date ,unit_id ,a.order_no ,convert(decimal(18,4),a.total_amount*red_flag) total_amount from T_Inout a
where 1=1 and a.order_type_id = '1F0A100C42484454BAEA211D4C14B80F' and a.order_reason_id = '2F6891A2194A4BBAB6F17B4C99A6C6F5'
a.unit_id in ( $UnitIds$ )
a.order_no like '%' + #order_no# + '%'
a.order_date >= #order_date_begin#
a.order_date <= #order_date_end#
) a group by a.order_date ,a.unit_id;
