statement.isNotEmpty:
insert into t_shift(shift_id,sales_user,pos_id,parent_shift_id,unit_id,deposit_amount ,sale_amount ,return_amount ,pos_date ,sales_qty ,sales_total_amount ,open_time ,close_time ,create_time ,create_user_id ,modify_time ,modify_user_id)
select shift_id,sales_user,pos_id,parent_shift_id,unit_id,deposit_amount ,sale_amount ,return_amount ,pos_date ,sales_qty ,sales_total_amount ,open_time ,close_time ,create_time ,create_user_id ,modify_time ,modify_user_id From (
SELECT #ShiftListInfo[].shift_id# shift_id ,#ShiftListInfo[].sales_user# sales_user ,#ShiftListInfo[].pos_id# pos_id ,#ShiftListInfo[].parent_shift_id# parent_shift_id ,#ShiftListInfo[].unit_id# unit_id ,#ShiftListInfo[].deposit_amount# deposit_amount ,#ShiftListInfo[].sale_amount# sale_amount ,#ShiftListInfo[].return_amount# return_amount ,#ShiftListInfo[].pos_date# pos_date ,#ShiftListInfo[].sales_qty# sales_qty ,#ShiftListInfo[].sales_total_amount# sales_total_amount ,#ShiftListInfo[].open_time# open_time ,#ShiftListInfo[].close_time# close_time
,#ShiftListInfo[].create_user_id# create_user_id ,#ShiftListInfo[].close_time# create_time ,#ShiftListInfo[].modify_user_id# modify_user_id ,#ShiftListInfo[].modify_time# modify_time
) P where p.shift_id not in (select shift_id from t_shift);
