delete t_cc_detail where order_id = #order_id#;
insert into t_cc_detail ( order_detail_id ,order_id ,ref_order_detail_id ,order_no ,stock_balance_id ,sku_id ,warehouse_id ,end_qty ,order_qty ,remark ,display_index ,create_time ,create_user_id ,modify_time ,modify_user_id ,if_flag ) SELECT a.order_detail_id ,a.order_id ,a.ref_order_detail_id ,a.order_no ,a.stock_balance_id ,a.sku_id ,a.warehouse_id ,a.end_qty ,a.order_qty ,a.remark ,a.display_index ,a.create_time ,a.create_user_id ,a.modify_time ,a.modify_user_id ,a.if_flag
FROM (
SELECT #CCDetailInfoList[].order_detail_id# order_detail_id ,#order_id# order_id ,#CCDetailInfoList[].ref_order_detail_id# ref_order_detail_id ,#CCDetailInfoList[].order_no# order_no ,#CCDetailInfoList[].stock_balance_id# stock_balance_id ,#CCDetailInfoList[].sku_id# sku_id ,#CCDetailInfoList[].warehouse_id# warehouse_id ,#CCDetailInfoList[].end_qty# end_qty ,#CCDetailInfoList[].order_qty# order_qty ,#CCDetailInfoList[].remark# remark ,#CCDetailInfoList[].display_index# display_index
,#create_time# create_time ,#create_user_id# create_user_id ,#modify_time# modify_time ,#modify_user_id# modify_user_id ,#CCDetailInfoList[].if_flag# if_flag
