statements.sql:
Declare @TmpTable Table
(brand_customer_id nvarchar(100)
,row_no int
);

insert into @TmpTable (brand_customer_id,row_no)
select x.brand_customer_id ,x.rownum_ From (
select
rownum_=row_number() over(order by a.brand_customer_code desc)
,brand_customer_id
from t_brand_customer a
where 1=1



      a.brand_customer_code like '%'+#brand_customer_code#+'%'




      a.brand_customer_name like '%'+#brand_customer_name#+'%'



) x ;

Declare @iCount int;

select @iCount = COUNT(*) From @TmpTable;

statements.select:
Declare @TmpTable Table (Customer_Id nvarchar(100) ,row_no int );
insert into @TmpTable (Customer_Id,row_no) select Customer_Id ,row_number() over(order by customer_id) row_no From T_MobileUser_Customer where 1=1 and isnull(if_flag,'0') = '0'
User_Id = #User_Id#
;
select a.* ,c.dd_name as customer_status_desc ,1 IsALD ,1 IsPad From t_customer a inner join @TmpTable b on(a.customer_id = b.customer_id) inner join vw_dic c on c.dic_code='status' and cast(a.customer_status as char)=c.dd_code left join t_user e on(a.create_user_id = e.user_id) left join t_user f on(a.modify_user_id = f.user_id) where b.row_no > #StartRow# and b.row_no <= #EndRow#;
