statement.isNotEmpty:
update t_item_price set status= '-1' ,modify_user_id = a.modify_user_id ,modify_time = a.modify_time ,if_flag = '0' From (
SELECT #Item_Id# item_id , #Create_User_Id# modify_user_id , #Create_Time# modify_time
) a where ( t_item_price.item_id = a.item_id);
update t_item_price set item_price = a.item_price ,modify_user_id = a.modify_user_id ,modify_time = a.modify_time ,status = '1' From (
SELECT #Item_Id# item_id , #Create_User_Id# modify_user_id , #Create_Time# modify_time , #ItemPriceList[].item_price_type_id# item_price_type_id , #ItemPriceList[].item_price_id# item_price_id , #ItemPriceList[].item_price# item_price
) a where ( t_item_price.item_id = a.item_id and t_item_price.item_price_type_id = a.item_price_type_id) or (t_item_price.item_price_id = a.item_price_id);
insert into t_item_price (item_price_id ,item_id ,item_price_type_id ,item_price ,status ,create_user_id ,create_time ,modify_user_id ,modify_time ) SELECT P.item_price_id ,P.item_id ,P.item_price_type_id ,P.item_price ,P.status ,P.create_user_id ,P.create_time ,P.modify_user_id ,P.modify_time
FROM (
SELECT #Item_Id# item_id , #Create_User_Id# modify_user_id , #Create_Time# modify_time , #ItemPriceList[].item_price_type_id# item_price_type_id , #ItemPriceList[].item_price_id# item_price_id , #ItemPriceList[].item_price# item_price , #Create_User_Id# create_user_id , #Create_Time# create_time , '1' status
) P left join t_item_price b on(P.item_price_id = b.item_price_id) left join t_item_price c on(P.item_id = c.item_id and P.item_price_type_id = c.item_price_type_id) where b.item_price_id is null and c.item_id is null;
insert into t_item_price_his (item_price_id ,item_id ,item_price_type_id ,item_price ,status ,create_user_id ,create_time ,modify_user_id ,modify_time ) SELECT P.item_price_id ,P.item_id ,P.item_price_type_id ,P.item_price ,P.status ,P.create_user_id ,P.create_time ,P.modify_user_id ,P.modify_time
FROM (
SELECT #Item_Id# item_id , #Create_User_Id# modify_user_id , #Create_Time# modify_time , #ItemPriceList[].item_price_type_id# item_price_type_id , #ItemPriceList[].item_price_id# item_price_id , #ItemPriceList[].item_price# item_price , #Create_User_Id# create_user_id , #Create_Time# create_time , '1' status
) P;

statements.select:
Declare @TmpTable Table (item_price_id nvarchar(100) ,row_no int );
insert into @TmpTable (item_price_id,row_no) select item_price_id ,row_number() over(order by item_price_id) row_no From T_Item_price where 1=1 and isnull(if_flag,'0') = '0' and item_price_type_id = #ItemPriceTypeId#;
select a.item_price_id ,a.item_id ,a.item_price_type_id ,a.item_price ,a.status ,a.create_user_id ,a.create_time ,a.modify_user_id ,a.modify_time ,(select item_price_type_name from t_item_price_type x where x.item_price_type_id = a.item_price_type_id) item_price_type_name From t_item_price a inner join @TmpTable b on(a.item_price_id = b.item_price_id) and b.row_no > #StartRow# and b.row_no <=#EndRow# ;
