statements.select:
Declare @TmpTable Table (sku_id nvarchar(100) ,item_price_type_id nvarchar(100) ,unit_id nvarchar(100) ,row_no int );
insert into @TmpTable (sku_id,item_price_type_id,unit_id,row_no) select sku_id ,item_price_type_id ,unit_id ,row_number() over(order by sku_id,item_price_type_id) row_no From vw_sku_price where 1=1 and isnull(if_flag,'0') = '0' and item_price_type_id = #ItemPriceTypeId#;
select a.sku_id ,a.item_id ,a.item_price_type_id ,a.price ,a.unit_id From vw_sku_price a inner join @TmpTable b on(a.sku_id = b.sku_id and a.item_price_type_id = b.item_price_type_id and isnull(a.unit_id,'1') = isnull(b.unit_id,'1')) and b.row_no > #StartRow# and b.row_no <=#EndRow# ;

update.isNotEmpty:
update t_item_price set bat_id = #bat_id# ,Modify_Time = #modify_time# ,Modify_User_Id = #modify_user_id# ,if_flag = '0' where item_price_type_id = #item_price_type_id# and item_id in (
SELECT #SkuPriceInfoList[].item_id# );
update t_sku_price set bat_id = #bat_id# ,Modify_Time = #modify_time# ,Modify_User_Id = #modify_user_id# ,if_flag = '0' where item_price_type_id = #item_price_type_id# and sku_id in (
SELECT #SkuPriceInfoList[].sku_id# );
