statements.select:
Declare @TmpTable Table (Shop_Id nvarchar(100) ,row_no int );
insert into @TmpTable (Shop_Id,row_no) select Shop_Id ,row_number() over(order by Shop_id) row_no From T_MobileUser_Shop where 1=1 and isnull(if_flag,'0') = '0'
User_Id = #User_Id#
;
select a.* ,d.customer_code ,d.customer_name ,de.dd_name as customer_status_desc ,c.dd_name as cs_status_desc ,'' as UnitId ,'' as unit_code ,'' as unit_name From t_customer_shop a inner join @TmpTable b on(a.customer_shop_id = b.shop_id) inner join vw_dic c on c.dic_code='status' and cast(a.cs_status as char)=c.dd_code inner join t_customer d on a.customer_id=d.customer_id inner join vw_dic de on de.dic_code='status' and cast(d.customer_status as char)=de.dd_code left join t_user e on(a.create_user_id = e.user_id) left join t_user f on(a.modify_user_id = f.user_id) where b.row_no > #StartRow# and b.row_no <= #EndRow#;
